import React, { useState, useEffect, useRef } from "react";
import { chatWithAI } from "../api";

const ChatBot = () => {
  const [input, setInput] = useState("");
  const [chat, setChat] = useState([]);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  const handleSend = async () => {
    if (!input.trim()) return;

    setLoading(true);

    const userMessage = input;
    setChat((prev) => [...prev, { role: "user", content: userMessage }]);
    setInput("");

    const response = await chatWithAI(userMessage);

    setChat((prev) => [
      ...prev,
      { role: "bot", content: response }
    ]);

    setLoading(false);
  };

  // Auto-scroll to bottom
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [chat, loading]);


  return (
    <div className="flex h-screen bg-gray-900 text-white">

      {/* SIDEBAR */}
      <div className="w-64 bg-gray-800 p-5 border-r border-gray-700 hidden md:block">
        <h2 className="text-xl font-semibold mb-4">AI Coach</h2>

        <button
          onClick={() => setChat([])}
          className="w-full bg-gray-700 hover:bg-gray-600 p-2 rounded-lg"
        >
          + New Chat
        </button>
      </div>

      {/* MAIN CHAT AREA */}
      <div className="flex-1 flex flex-col">
        
        {/* Header */}
        <div className="p-4 border-b border-gray-700 bg-gray-800 text-center text-xl font-semibold">
          Chat with Your AI Coach ðŸ¤–
        </div>

        {/* Chat messages */}
        <div className="flex-1 overflow-y-auto p-6 space-y-4">
          {chat.map((msg, index) => (
            <div
              key={index}
              className={`flex ${
                msg.role === "user" ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`max-w-xl p-3 rounded-xl shadow 
                ${
                  msg.role === "user"
                    ? "bg-blue-600 text-white"
                    : "bg-gray-700 text-gray-200"
                }`}
                dangerouslySetInnerHTML={{ __html: msg.content }}
              />
            </div>
          ))}

          {loading && (
            <div className="text-gray-400">AI is thinking...</div>
          )}

          <div ref={chatEndRef} />
        </div>

        {/* Input */}
        <div className="p-4 border-t border-gray-700 bg-gray-800 flex gap-3">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Send a message..."
            className="flex-1 p-3 rounded-lg bg-gray-700 text-white border border-gray-600 focus:outline-none"
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
          />

          <button
            onClick={handleSend}
            className="bg-blue-600 hover:bg-blue-700 px-5 py-3 rounded-lg font-semibold"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatBot;
