import React, { useState } from "react";
import { chatWithAI } from "../api";

const ChatBot = () => {
  const [input, setInput] = useState("");
  const [chat, setChat] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (!input.trim()) return;
    setLoading(true);
    const response = await chatWithAI(input);
    setChat([...chat, { user: input, bot: response }]);
    setInput("");
    setLoading(false);
  };

  return (
    <div className="p-6 bg-white shadow-lg rounded-2xl max-w-md mx-auto mt-10 border border-gray-200">
      <h2 className="text-2xl font-bold mb-4 text-center text-blue-600">
        Chat with Your AI Coach ðŸ¤–
      </h2>

      <div className="h-64 overflow-y-auto border p-3 mb-4 rounded bg-gray-50">
        {chat.map((msg, i) => (
          <div key={i} className="mb-3">
            <p className="text-green-700 font-semibold">You: {msg.user}</p>
            <p className="text-gray-800">Coach: {msg.bot}</p>
          </div>
        ))}
        {loading && <p className="text-blue-500">Thinking...</p>}
      </div>

      <div className="flex gap-2">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask about workouts or diet..."
          className="flex-1 border rounded p-2 focus:outline-blue-400"
        />
        <button
          onClick={handleSend}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatBot;
