import React, { useState, useEffect, useRef } from "react";
import { chatWithAI } from "../api";

const ChatBot = () => {
  const [input, setInput] = useState("");
  const [chats, setChats] = useState([]); // All chats
  const [activeChatId, setActiveChatId] = useState(null);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  // Load all chats from localStorage
  useEffect(() => {
    const storedChats = JSON.parse(localStorage.getItem("allChats")) || [];
    setChats(storedChats);

    if (storedChats.length > 0) {
      setActiveChatId(storedChats[0].id); // Load first chat by default
    }
  }, []);

  // Save chats whenever changed
  useEffect(() => {
    localStorage.setItem("allChats", JSON.stringify(chats));
  }, [chats]);

  // Get active chat
  const activeChat = chats.find((c) => c.id === activeChatId);

  // Auto-scroll
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [activeChat, loading]);

  // Create a new chat
  const createNewChat = () => {
    const newChat = {
      id: Date.now(),
      title: "New Chat",
      messages: []
    };
    setChats((prev) => [newChat, ...prev]);
    setActiveChatId(newChat.id);
  };

  // Send message
  const handleSend = async () => {
    if (!input.trim()) return;

    setLoading(true);
    const userInput = input;

    // Add user message
    const updatedChats = chats.map((chat) => {
      if (chat.id === activeChatId) {
        return {
          ...chat,
          messages: [...chat.messages, { role: "user", content: userInput }]
        };
      }
      return chat;
    });

    setChats(updatedChats);
    setInput("");

    // Get bot reply
    const response = await chatWithAI(userInput);

    // Update chat title + bot message
    const finalUpdatedChats = updatedChats.map((chat) => {
      if (chat.id === activeChatId) {
        return {
          ...chat,
          title:
            chat.title === "New Chat"
              ? userInput.split(" ").slice(0, 5).join(" ") + "..."
              : chat.title,
          messages: [...chat.messages, { role: "bot", content: response }]
        };
      }
      return chat;
    });

    setChats(finalUpdatedChats);
    setLoading(false);
  };

  return (
    <div className="flex h-screen bg-gray-900 text-white">

      {/* Sidebar */}
      <div className="w-72 bg-gray-800 p-4 border-r border-gray-700">
        <button
          onClick={createNewChat}
          className="w-full bg-gray-700 hover:bg-gray-600 p-3 rounded-lg mb-4"
        >
          + New Chat
        </button>

        <h3 className="text-gray-400 mb-2">Chat History</h3>

        <div className="space-y-2 overflow-y-auto h-[80vh] pr-2">
          {chats.map((chat) => (
            <div
              key={chat.id}
              onClick={() => setActiveChatId(chat.id)}
              className={`p-3 rounded-lg cursor-pointer ${
                chat.id === activeChatId
                  ? "bg-gray-700 border border-gray-600"
                  : "bg-gray-700/40 hover:bg-gray-700"
              }`}
            >
              <p className="truncate">{chat.title}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Chat Area */}
      <div className="flex-1 flex flex-col">
        
        <div className="p-4 border-b border-gray-700 bg-gray-800 text-center text-xl font-semibold">
          AI Fitness Coach ü§ñ
        </div>

        <div className="flex-1 overflow-y-auto p-6 space-y-4">

          {!activeChat && (
            <p className="text-gray-400 text-center mt-20">
              Click ‚ÄúNew Chat‚Äù to begin
            </p>
          )}

          {activeChat &&
            activeChat.messages.map((msg, index) => (
              <div
                key={index}
                className={`flex ${
                  msg.role === "user" ? "justify-end" : "justify-start"
                }`}
              >
                <div
                  className={`max-w-xl p-3 rounded-xl shadow ${
                    msg.role === "user"
                      ? "bg-blue-600 text-white"
                      : "bg-gray-700 text-gray-200"
                  }`}
                  dangerouslySetInnerHTML={{ __html: msg.content }}
                />
              </div>
            ))}

          {loading && <p className="text-gray-400">AI is thinking...</p>}

          <div ref={chatEndRef} />
        </div>

        {/* Input Section */}
        <div className="p-4 border-t border-gray-700 bg-gray-800 flex gap-3">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Ask something..."
            className="flex-1 p-3 rounded-lg bg-gray-700 text-white border border-gray-600 focus:outline-none"
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
          />

          <button
            onClick={handleSend}
            className="bg-blue-600 hover:bg-blue-700 px-5 py-3 rounded-lg font-semibold"
          >
            Send
          </button>
        </div>

      </div>
    </div>
  );
};

export default ChatBot;
